import React, { useState } from 'react';
import axios from 'axios';
import './App.css';

function App() {
  const [url, setUrl] = useState('');
  const [result, setResult] = useState(null);
  const [error, setError] = useState('');
  const [loading, setLoading] = useState(false);

  const handleAnalyze = async () => {
    const videoId = url.split('v=')[1]?.split('&')[0];
    if (!videoId) {
      setError('Please enter a valid YouTube URL');
      return;
    }

    setLoading(true);
    setError('');
    setResult(null);

    try {
      const response = await axios.get(`http://localhost:5000/analyze/${videoId}`);
      if (response.data.error) {
        setError(response.data.error); // Display backend error (e.g., "No transcript available")
      } else {
        setResult(response.data); // Success case
      }
    } catch (err) {
      setError('Failed to connect to the backend—check if it’s running!');
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="App">
      <h1>YouTube Analytics Dashboard</h1>
      <div className="input-section">
        <input
          type="text"
          value={url}
          onChange={(e) => setUrl(e.target.value)}
          placeholder="Enter YouTube URL"
          disabled={loading}
        />
        <button onClick={handleAnalyze} disabled={loading}>
          {loading ? 'Analyzing...' : 'Analyze'}
        </button>
      </div>
      {error && <p className="error">{error}</p>}
      {result && !result.error && (
        <div className="results">
          <p><strong>Transcript:</strong> {result.transcript}</p>
          <p><strong>Sentiment:</strong> {result.sentiment}</p>
          <p><strong>Top Keywords:</strong> {result.keywords.join(', ')}</p>
        </div>
      )}
    </div>
  );
}

export default App;
